export default {
  props: {
    todos: {
      type: Array,
      required: true
    }
  },
  data() {
    return {
      title: ''
    };
  },
  methods: {
    onSubmit() {
      const trimmedTitle = this.title.trim().toUpperCase();
      if (trimmedTitle === '') {
        return;
      }
      const titleExists = this.todos.some(todo => todo.title.toUpperCase() === trimmedTitle);
      if (titleExists) {
        alert("Title already exists!");
        return;
      }
      if (this.title.trim()) {
        const newTodo = {
          id: Date.now(),
          title: this.title,
          completed: false
        };
        this.$emit('add-todo', newTodo);
        this.title = '';
      }
    }
  }
};
