export default {
  props: {
    todos: ['todos']
  },
  data() {
    return {
      title: ''
    };
  },
  methods: {
    onSubmit() {
      const trimmedTitle = this.title.trim();
      if (trimmedTitle === '') {
        return;
      }
      if (this.title.trim()) {
        const newTodo = {
          id: Date.now(),
          title: this.title,
          complited: false
        };
        this.$emit('add-todo', newTodo);
        this.title = '';
      }
    }
  }
};
